import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";

const GRID_SIZE = 5;
const START_TILES = 2;

function generateEmptyGrid() {
  return Array(GRID_SIZE)
    .fill(null)
    .map(() => Array(GRID_SIZE).fill(null));
}

function spawnTile(grid) {
  const empty = [];
  for (let i = 0; i < GRID_SIZE; i++) {
    for (let j = 0; j < GRID_SIZE; j++) {
      if (!grid[i][j]) empty.push([i, j]);
    }
  }
  if (empty.length === 0) return grid;
  const [i, j] = empty[Math.floor(Math.random() * empty.length)];
  grid[i][j] = Math.random() < 0.9 ? 1 : 2;
  return [...grid];
}

function EntropyGrid() {
  const [grid, setGrid] = useState(generateEmptyGrid());

  useEffect(() => {
    let newGrid = [...grid];
    for (let i = 0; i < START_TILES; i++) {
      newGrid = spawnTile(newGrid);
    }
    setGrid(newGrid);
  }, []);

  function handleKeyDown(e) {
    // Movement and merging logic placeholder
    console.log("Key pressed:", e.key);
  }

  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [grid]);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Entropy Grid</h1>
      <div className="grid grid-cols-5 gap-1">
        {grid.flat().map((val, idx) => (
          <div
            key={idx}
            className="w-16 h-16 flex items-center justify-center bg-gray-200 rounded text-xl"
          >
            {val ?? ""}
          </div>
        ))}
      </div>
      <button onClick={() => setGrid(spawnTile([...grid]))} className="mt-4 bg-blue-500 text-white px-4 py-2 rounded">
        Spawn Tile
      </button>
    </div>
  );
}

export default EntropyGrid;
